if (value === 1) {
    return "HOME SCREEN";
} else if (value === 2) {
    return "ENTER EMPLOYEE CODE";
} else if (value === 3) {
    return "SCAN ENGINE NO. TO INITIATE CYCLE";
} else if (value === 4) {
    return "WRONG BAR CODE SCAN TRY AGAIN";
} else if (value === 5) {
    return "DIESEL ENGINE BARCODE SCAN OK, START ENGINE";
} else if (value === 6) {
    return "CNG ENGINE BARCODE SCAN OK, START ENGINE";
} else if (value === 7) {
    return "WATER NOT PRESENT, CHECK WATER PRESENCE";
} else if (value === 8) {
    return "OIL PRESSURE LOW, PRESS EMERGENCY STOP";
} else if (value === 9) {
    return "DO TAPPET SETTING";
} else if (value === 10) {
    return "TESTING UNDER PROCESSING";
} else if (value === 11) {
    return "CALIBRATION SETTING WINDOW ON";
} else if (value === 12) {
    return "MANUAL SETTING-1";
} else if (value === 13) {
    return "MANUAL SETTING-2";
} else if (value === 14) {
    return "ENGINE RPM OVER SOOT ALARM";
} else if (value === 15) {
    return "HISTORY LOG OPENED";
} else if (value === 16) {
    return "EMERGENCY STOP BUTTON ON";
} else if (value === 17) {
    return "TEST CYCLE COMPLETED, WAIT FOR TEST RESULTS";
} else if (value === 18) {
    return "THERMOSTAT VALVE NOT OPENED, WAIT FOR CHANGE IN STATUS";
} else if (value === 19) {
    return "THERMOSTAT VALVE NOT OPENED, TEST FAILED";
} else if (value === 20) {
    return "COOLANT TEMPERATURE HIGH, PRESS EMERGENCY STOP";
} else if (value === 21) {
    return "OIL PRESSURE HIGH, PRESS EMERGENCY STOP";
} else if (value === 22) {
    return "CYCLE RESUMED, START ENGINE";
} else if (value === 23) {
    return "AT 2500 RPM, OIL PRESSURE LOW";
} else if (value === 24) {
    return "AT SECOND IDLE RPM, COOLANT TEMPERATURE NOT OR OIL PRESSURE LOW";
} else {
    return "INVALID VALUE";
}
